'use strict';
describe('SpiritTest class', () => {
    let spirittests, spyObj;
    var createdTestRan = false;
    global.called = () => {
        createdTestRan = true;
    };
    spirittests = require('./spirittest.class.js');
    spirittests.createTest(function() {
        describe('A spec', () => {
            it('Should do something', () => {
                global.called();
            });
        });
    });
    beforeEach(() => {
        spyObj = {
            'testFn': () => {
            }
        };
        spyOn(spyObj, 'testFn');
        spirittests.readTests();
    });
    it('Should read in test files', () => {
        expect(spirittests.files.length).toBe(1);
    });
    spirittests.initialize();
    it('Should have run the included tests', () => {
        expect(createdTestRan).toBe(true);
    });
    afterEach(() => {
        process.emit('SpiritFiles Cleanup');
    });
});