module.exports = function(req) {
	return (typeof req.connection.remoteAddress != "undefined" && (req.connection.remoteAddress == "127.0.0.1" || req.connection.remoteAddress.substr(-"127.0.0.1".length) == "127.0.0.1" || req.connection.remoteAddress == "::1"  || req.connection.remoteAddress == "::ffff:127.0.0.1"));
}