module.jsonCache	= {};
module.exports	= function(file,cache){
	if ( typeof cache == "undefined" )
		cache	 =false;
	if ( cache && module.jsonCache.hasOwnProperty(file) )
		return module.jsonCache[file];

	if ( !fs.existsSync(file) ) {
		fs.closeSync(fs.openSync(file,'w'));
		fs.writeFileSync(file,'{}');
	}
	if ( fs.existsSync(file) ) {
		module.jsonCache[file]	= JSON.parse(fs.readFileSync(file,'UTF8'));
		return module.jsonCache[file];
	}
	return {};
}