module.exports = function(host){
	var ps, self, signal;
    let timestamp = (new Date()).getTime()

	ps	= require('portscanner');
	self = this;
	signal	= this.wait();

    // first establish that the hostnameost is reachable
    url    = this.answer('url-parts',host);

    ps.checkPortStatus(url.port, url.hostname, function(error, status) {
        let ms;
        if (status == "open") {
            ms = (new Date()).getTime() - timestamp;
        } else {
            ms = false;
        }
        self.finish(signal, ms);
    });
    return signal;
};