/**
 * @file
 * This file should build the dependencies using the SpiritDependencies class.
 */
if (process.cwd().indexOf('node_modules') != -1) {
    process.chdir(require('spiritfiles').getApplicationDirectory());
}
try {
    let modules = [];
    if (process.argv.length > 2) {
        modules = process.argv.slice(2);
    } else {
        modules = [];
    }
    require(__dirname + '/index.js').moveModules(modules)
        .then(() => {
            require(__dirname + '/index.js').buildPackageJSON();
            process.exit();
        });
} catch(e) {
}