/**
 * @file
 * This file should create a "configs" folder in the calling directory.
 */
let fs = require('fs');
let path;
if (fs.existsSync(__dirname + '/../../package.json')) {
    path = __dirname + '/../../';
} else {
    path = __dirname + '/';
}
if (!fs.existsSync(path + 'configs')) {
    fs.mkdirSync(path + 'configs');
}