"use strict";

class SpiritFilesTestHelpers {
    constructor() {
        this.spiritfiles = require(__dirname + '/../index.js');
    }
    createFiles() {
        this.spiritfiles.addFiles({
            'spiritfilesFiles/atest.json': '{"a": "b"}',
            'spiritfilesFiles/ztest.json': '{"a": "b"}',
            'spiritfilesFiles/subdir/atest.json': '{"a": "b"}',
            'node_modules/spiritfilesFilesTestModule/spiritfilesFiles/atest.json':
                '{"a": "123", "b": "c"}',
            'node_modules/spiritfilesFilesTestModule/spiritfilesFiles/ztest.json':
                '{"a": "234", "b": "d"}',
            'node_modules/spiritfilesFilesTestModule/spiritfilesFiles/node_modules/nestedFolder/spiritFilesFiles/atest.json':
                '{"b": "d", "c": "d"}',
            'node_modules/spiritfilesFilesTestModule/spiritfilesFiles/node_modules/nestedFolder/spiritFilesFiles/ztest.json':
                '{"b": "123", "c": "345"}',
            'node_modules/spiritfilesFilesTestModule/spiritfilesFiles/node_modules/nestedFolder/spiritFilesFiles/subdir/ztest.json':
                '{"b": "e", "c": "f"}'
        });
    }

    cleanupFiles() {
        this.spiritfiles.rrmdir('spiritfilesFiles');
        this.spiritfiles.rrmdir('node_modules/spiritfilesFilesTestModule');
    }
}
module.exports = new SpiritFilesTestHelpers();