let chalk = require('chalk');
let spirittests = require(__dirname + '/spirittest.class.js');
spirittests.initialize();
if (spirittests.customRunner) {
    // else if included via  custom test runner, execute Jasmine and export
    module.exports = require(__dirname + '/spirittest.class.js').execute() 
        .then((passed) => {
            if (passed) {
                console.log('SpiritTests: ' + chalk.green('Passed'));
            } else {
                console.log('SpiritTests: ' + chalk.red('Failed'));
            }

            process.exit(); // let's exit now!
        }).catch((e) => {
            console.log('SpiritTests: oops, something went wrong.', e);
        });
}