class SpiritTimers {
    constructor() {
        this.setupNav();
    }

    /**
     * Changes page to a name iframe window.
     * @param {String} to The page name to change to.
     */
    changePage(to) {
        $('.page').hide();
        $('.page-' + to).show();
        $('.nav').removeClass('active');
        $('.nav-' + to).addClass('active');
    }

    /**
     * Sets of navigation events, including click events.
     */
    setupNav() {
        $hashRouter.on({}, (hash) => {
            this.changePage(hash.page);
        });
    }
}